//********************************** MODEL CLASSES FOR UNHAPPY PATHS START ****************************************
public interface FailureScenario{
	public String getEanNo();
	public String getErrorCode();
	public String getKey();
}

public abstract class AbstractFailureScenario implements FailureScenario{
	protected String eanNo;
	protected String errorCode;

	public String getEanNo(){
		return eanNo;
	}

	public String getErrorCode(){
		return errorCode;
	}
}

public class CCSDFailureScenario extends AbstractFailureScenario{
	protected Set<String> unavailableStores;

	public CCSDFailureScenario(String eanNoInput,String errorCodeInput,String[] unavailableCcsdStoresInput){
		eanNo = eanNoInput;
		errorCode = errorCodeInput;
		unavailableStores = new HashSet<String>(Arrays.asList(unavailableCcsdStoresInput));
	}

	public Set<String> getUnavailableStores(){
		return unavailableStores;
	}

	public String getKey(){
		return getEanNo() + "_ZPS";
	}
}

public class CCNDFailureScenario extends AbstractFailureScenario{

	public CCNDFailureScenario(String eanNoInput,String errorCodeInput){
		eanNo = eanNoInput;
		errorCode = errorCodeInput;
	}

	public String getKey(){
		return getEanNo() + "_ZVS";
	}
}

public class HDZVHFailureScenario extends AbstractFailureScenario{

	public HDZVHFailureScenario(String eanNoInput,String errorCodeInput){
		eanNo = eanNoInput;
		errorCode = errorCodeInput;
	}

	public String getKey(){
		return getEanNo() + "_ZVH";
	}
}

public class HDZBKSFailureScenario extends AbstractFailureScenario{

    public HDZBKSFailureScenario(String eanNoInput,String errorCodeInput){
        eanNo = eanNoInput;
        errorCode = errorCodeInput;
    }

    public String getKey(){
        return getEanNo() + "_ZBKS";
    }
}

public class HDZSDFailureScenario extends AbstractFailureScenario{
	protected Set<String> unavailableStores;

	public HDZSDFailureScenario(String eanNoInput,String errorCodeInput,String[] unavailableCcsdStoresInput){
		eanNo = eanNoInput;
		errorCode = errorCodeInput;
		unavailableStores = new HashSet<String>(Arrays.asList(unavailableCcsdStoresInput));
	}

	public Set<String> getUnavailableStores(){
		return unavailableStores;
	}

	public String getKey(){
		return getEanNo() + "_ZSD";
	}
}

