	public static void loadConstantsForNewRequest(){

		groovyUtils = new com.eviware.soapui.support.GroovyUtils(InvokedVariablesContainer.context);
		rootHolder = groovyUtils.getXmlHolder(InvokedVariablesContainer.mockRequest.requestContent);
		requestXml = rootHolder.getXml();
		soapenvEnvelopeRootNodeRequest = new XmlParser().parseText(requestXml);
		soapenvBodyRootNodeRequest = soapenvEnvelopeRootNodeRequest[soapenvns.Body];
		processFulfilmentSourceRootNodeRequest = soapenvBodyRootNodeRequest[ordnsReq.ProcessFulfilmentSource];
		applicationAreaRootNodeRequest = processFulfilmentSourceRootNodeRequest[nsnsReq.ApplicationArea];
		dataAreaRootNodeRequest = processFulfilmentSourceRootNodeRequest[ordnsReq.DataArea];
		fulfilmentSourceRootNodeRequest = dataAreaRootNodeRequest[ordnsReq.FulfilmentSource];

		unavailableItemsMap.clear();
		Properties pfsMockResponseProperties = new Properties();
		FileInputStream inputStream = new FileInputStream(dynamicTemplateLocation + propertyFileName);
		pfsMockResponseProperties.load(inputStream);

		String ccsdUnhappyPathsStr = pfsMockResponseProperties.getProperty("UNHAPPY_CCSDEAN_ERRORCODE_UNAVAILABLESTORES");
		if(ccsdUnhappyPathsStr != null && ccsdUnhappyPathsStr.trim().length() > 0){
			String[] unavailableItemsWithRelatedData = ccsdUnhappyPathsStr.split(",");

			for(String unavailableItemData : unavailableItemsWithRelatedData){

				String[] eanWithErrCodeAndUnavailableStores = unavailableItemData.split(":");
				if(eanWithErrCodeAndUnavailableStores.length == 3){

					String eanNo = eanWithErrCodeAndUnavailableStores[0];
					String errorCode = eanWithErrCodeAndUnavailableStores[1];
					String[] unavailableCcsdStores = eanWithErrCodeAndUnavailableStores[2].split("-");
					FailureScenario ccsdFailureScenario = new CCSDFailureScenario(eanNo,errorCode,unavailableCcsdStores);
					unavailableItemsMap.put(ccsdFailureScenario.getKey(),ccsdFailureScenario);
				}
			}
		}

		String ccndUnhappyPathsStr = pfsMockResponseProperties.getProperty("UNHAPPY_CCNDEAN_ERRORCODE");
		if(ccndUnhappyPathsStr != null && ccndUnhappyPathsStr.trim().length() > 0){

			String[] unavailableCcndItemsWithRelatedData = ccndUnhappyPathsStr.split(",");

			for(String unavailableCcndItemData : unavailableCcndItemsWithRelatedData){
				String[] eanWithErrCode = unavailableCcndItemData.split(":");
				if(eanWithErrCode.length == 2){
					String eanNo = eanWithErrCode[0];
					String errorCode = eanWithErrCode[1];
					FailureScenario ccndFailureScenario = new CCNDFailureScenario(eanNo,errorCode);
					unavailableItemsMap.put(ccndFailureScenario.getKey(),ccndFailureScenario);
				}
			}
		}

		String hdDfsUnhappyPathsStr = pfsMockResponseProperties.getProperty("UNHAPPY_HDDFSEAN_ERRORCODE_UNAVAILABLESTORES");
		if(hdDfsUnhappyPathsStr != null && hdDfsUnhappyPathsStr.trim().length() > 0){
			String[] unavailableItemsWithRelatedData = hdDfsUnhappyPathsStr.split(",");

			for(String unavailableItemData : unavailableItemsWithRelatedData){

				String[] eanWithErrCodeAndUnavailableStores = unavailableItemData.split(":");
				if(eanWithErrCodeAndUnavailableStores.length == 3){

					String eanNo = eanWithErrCodeAndUnavailableStores[0];
					String errorCode = eanWithErrCodeAndUnavailableStores[1];
					String[] unavailableCcsdStores = eanWithErrCodeAndUnavailableStores[2].split("-");
					FailureScenario hdZsdFailureScenario = new HDZSDFailureScenario(eanNo,errorCode,unavailableCcsdStores);
					unavailableItemsMap.put(hdZsdFailureScenario.getKey(),hdZsdFailureScenario);
				}
			}
		}

		String hdZvhUnhappyPathsStr = pfsMockResponseProperties.getProperty("UNHAPPY_HDDSVEAN_ERRORCODE");
		if(hdZvhUnhappyPathsStr != null && hdZvhUnhappyPathsStr.trim().length() > 0){

			String[] unavailableHdZvhItemsWithRelatedData = hdZvhUnhappyPathsStr.split(",");

			for(String unavailableHdZvhItemData : unavailableHdZvhItemsWithRelatedData){
				String[] eanWithErrCode = unavailableHdZvhItemData.split(":");
				if(eanWithErrCode.length == 2){
					String eanNo = eanWithErrCode[0];
					String errorCode = eanWithErrCode[1];
					FailureScenario hdZvhFailureScenario = new HDZVHFailureScenario(eanNo,errorCode);
					unavailableItemsMap.put(hdZvhFailureScenario.getKey(),hdZvhFailureScenario);
				}
			}
		}

    String hdZbksUnhappyPathsStr = pfsMockResponseProperties.getProperty("UNHAPPY_HDDSVEAN_ERRORCODE");
        if(hdZbksUnhappyPathsStr != null && hdZbksUnhappyPathsStr.trim().length() > 0){

            String[] unavailableHdZbksItemsWithRelatedData = hdZbksUnhappyPathsStr.split(",");

            for(String unavailableHdZbksItemData : unavailableHdZbksItemsWithRelatedData){
                String[] eanWithErrCode = unavailableHdZbksItemData.split(":");
                if(eanWithErrCode.length == 2){
                    String eanNo = eanWithErrCode[0];
                    String errorCode = eanWithErrCode[1];
                    FailureScenario hdZbksFailureScenario = new HDZBKSFailureScenario(eanNo,errorCode);
                    unavailableItemsMap.put(hdZbksFailureScenario.getKey(),hdZbksFailureScenario);
                }
            }
        }		

		InvokedVariablesContainer.log.info(unavailableItemsMap);

		String ccndScrewfiwIdsStr = pfsMockResponseProperties.getProperty("CCND_SCREWFIX_SUPPLIER_IDS");
		if(ccndScrewfiwIdsStr != null && ccndScrewfiwIdsStr.trim().length() > 0){
			String[] ccndScrewfixIdArray = ccndScrewfiwIdsStr.split(",");
			ccndScrewfixIds = Arrays.asList(ccndScrewfixIdArray);
			Collections.shuffle(ccndScrewfixIds);
			InvokedVariablesContainer.log.info(ccndScrewfixIds);
		}
	}
}

