public class FulfilmentSourceLinesRequestTag extends AbstractGenericRequestTag{

	private static Map<String,Set<String>> deliveryGrpCodeMap = new HashMap<String,Set<String>>();
	private static Map<String,ItemSupplier> itemSupplierMap = new HashMap<String,ItemSupplier>();
	private static Map<String,String> fulfilmentSourceTypeMap = new HashMap<String,String>();

	static {

		deliveryGrpCodeMap.put("ZVH","V01");
		deliveryGrpCodeMap.put("ZVS","C01");
		deliveryGrpCodeMap.put("ZPS","P01");
		deliveryGrpCodeMap.put("ZSD","S01");
                deliveryGrpCodeMap.put("ZBFC","V01");
                deliveryGrpCodeMap.put("ZBKS","V01");
                deliveryGrpCodeMap.put("ZFCO","V01");

		fulfilmentSourceTypeMap.put("ZVH","DSV - Delivery to Home");
		fulfilmentSourceTypeMap.put("ZVS","DSV - Pay and Collect");
		fulfilmentSourceTypeMap.put("ZPS","ClickAndCollectSameDay");
		fulfilmentSourceTypeMap.put("ZSD","Store - Delivery to Home");
                fulfilmentSourceTypeMap.put("ZBFC","FC");
                fulfilmentSourceTypeMap.put("ZBKS","Bulk");
                fulfilmentSourceTypeMap.put("ZFCO","Bulk");

		itemSupplierMap.put("ZVH",new ItemSupplier("UNORH","0000502405"));
		itemSupplierMap.put("ZSD",new ItemSupplier("UNORH","0000502405"));

		if(Constants.ccndScrewfixIds.size() > 0){
			InvokedVariablesContainer.log.info("############### LLLLLLLLLLLLLLLLLLL   111111");
			itemSupplierMap.put("ZVS",new ItemSupplier("ECO2418TR",Constants.ccndScrewfixIds.get(0)));
		}
		else{
			itemSupplierMap.put("ZVS",new ItemSupplier("ECO2418TR","0000501301"));
		}
	}

	public FulfilmentSourceLinesRequestTag(def node){
		InvokedVariablesContainer.log.info("@@@@@@@@@@@@@@@@@ CREATING FulfilmentSourceLinesRequestTag instance");
		this.rootNode = node;
	}

	public String getSupplierItemID(){
		ItemSupplier itemSupplier = itemSupplierMap.get(getShippingMethodCode());
		if(itemSupplier != null){
			return (itemSupplier.getSupplierItemID());
		}
		return "";
	}

	public String getSupplierPartyId(){
		ItemSupplier itemSupplier = itemSupplierMap.get(getShippingMethodCode());
		if(itemSupplier != null){
			return (itemSupplier.getSupplierPartyId());
		}
		return "";
	}

	public boolean isNodeDeletable(String hierarchy){
		InvokedVariablesContainer.log.info("################ isNodeDeletable : " + getShippingMethodCode() + "_" + getProcessingStatus());
		Set deletableNodesSet = deletedNodesMap.get(getShippingMethodCode() + "_" + getProcessingStatus());
		return (deletableNodesSet != null && deletableNodesSet.contains(hierarchy));
	}

	public String getStatusCode(){
		InvokedVariablesContainer.log.info("####################### getStatusCode 111111111");
		if("Success".equals(getProcessingStatus())){
			InvokedVariablesContainer.log.info("####################### getStatusCode 22222222");
			return "0000";
		}
		else{
			InvokedVariablesContainer.log.info("####################### getStatusCode 33333333333");
			String errorKey = getItemId() + "_" + getShippingMethodCode();
			FailureScenario failureScenario = Constants.unavailableItemsMap.get(errorKey);
			InvokedVariablesContainer.log.info("####################### getStatusCode 444444444444");
			return failureScenario.getErrorCode();
		}
	}

	public String getStatusDescription(){
		InvokedVariablesContainer.log.info("####################### getStatusDescription 111111111111111111");
		if("Success".equals(getProcessingStatus())){
			InvokedVariablesContainer.log.info("####################### getStatusDescription 22222222222222");
			return "Line item processed successfully";
		}
		else{
			InvokedVariablesContainer.log.info("####################### getStatusDescription 33333333333333333333");
			String errorKey = getItemId() + "_" + getShippingMethodCode();
			InvokedVariablesContainer.log.info("####################### getStatusDescription 444444444444444444444444444   " + errorKey);
			FailureScenario failureScenario = Constants.unavailableItemsMap.get(errorKey);
			InvokedVariablesContainer.log.info("####################### getStatusDescription 55555555555555555555");
			String errCode = failureScenario.getErrorCode();
			InvokedVariablesContainer.log.info("####################### getStatusDescription 666666666666666666666666666666");
			return Constants.errorCodeDescriptionMap.get(errCode);
		}
	}

	public String getStatusType(){
		if("Success".equals(getProcessingStatus())){
			return "SUCCESS";
		}
		else{
			return "FAILURE";
		}
	}

	public String getStatusTypeNameAttribute(){
		String statusTypeNameAttributeKey = getShippingMethodCode() + "_" + getProcessingStatus() + "_Type_Name_Attribute";
		String  statusTypeNameAttribute = statusMap.get(statusTypeNameAttributeKey);
		return statusTypeNameAttribute;
	}

	public String getShippingMethodCode(){
		def shippingMethodCode = rootNode[Constants.ordnsRes.ShippingMethod][Constants.oansRes.CategoryCodes][Constants.oansRes.Code].text();
		return shippingMethodCode;
	}

	public String getShippingMethodCategoryCodeForResponse(){
		if("ZPS".equals(getShippingMethodCode())){
			return "Z013";
		}
		else if("ZSD".equals(getShippingMethodCode())){
			return "Z015";
		}
		else{
			return getShippingMethodCode();
		}
	}

	public String getLineNumber(){
		def lineNumber = rootNode[Constants.ordnsRes.SalesOrderReference][Constants.oansRes.LineNumber].text();
		return lineNumber;
	}

	public String getItemId(){
		def itemId = rootNode[Constants.ordnsRes.ProductItem][Constants.oansRes.UPCID].text();
		return itemId;
	}

	public String getAtgItemId(){
		def itemId = rootNode[Constants.ordnsRes.ProductItem][Constants.oansRes.ItemID][Constants.oansRes.ID].text();
		return itemId;
	}

	public String getRequiredDateTime(){
		def requiredDateTime = rootNode[Constants.oansRes.RequiredDateTime].text();
		return requiredDateTime;
	}

	public String getRequiredQuantityAvailableDateTimeInResponseForCCSD(){
		if("Success".equals(getProcessingStatus())){
			return getRequiredDateTime();
		}
		else{
			return "";
		}
	}

	public String getRequiredQuantity(){
		def requiredQuantity = rootNode[Constants.oansRes.RequiredQuantity].text();
		return requiredQuantity;
	}

	public String getCurrentAvailableQuantityInResponseTag(){
		if("Success".equals(getProcessingStatus())){
			InvokedVariablesContainer.log.info(getRequiredQuantity());
			return getRequiredQuantity();
		}
		else {
			String errorKey = getItemId() + "_" + getShippingMethodCode();
			FailureScenario failureScenario = Constants.unavailableItemsMap.get(errorKey);
			String errCode = failureScenario.getErrorCode();
			if("2082".equals(errCode)){
				return "" + (Integer.parseInt(getRequiredQuantity()) - 1);
			}
			else if("2081".equals(errCode)){
				return "0";
			}
			else if("2044".equals(errCode)){
				return "" + (Integer.parseInt(getRequiredQuantity()) - 1);
			}


		}
	}

	public String getDeliveryGroupCodeForResponse(){
		return deliveryGrpCodeMap.get(getShippingMethodCode());
	}

	public String getFulfilmentSourceTypeForResponse(){
		return fulfilmentSourceTypeMap.get(getShippingMethodCode());
	}

	public String getCCNDDeliveryDateTimeForResponse(){
		Date today = new Date();
		Calendar cal = Calendar.getInstance();
        cal.setTime(today);
        cal.add(Calendar.DATE, 1); //minus number would decrement the days
        cal.add(Calendar.HOUR_OF_DAY, 16 - cal.get(Calendar.HOUR_OF_DAY));
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
        SimpleDateFormat sdf1 = new SimpleDateFormat("HH:mm:ss");
        return sdf.format(cal.getTime()) + "T" + sdf1.format(cal.getTime());
	}

	public String getCreateByDateTimeForResponse(){
	   Date today = new Date();
	   Calendar cal = Calendar.getInstance();
        cal.setTime(today);
        SimpleDateFormat sdfDate = new SimpleDateFormat("yyyy-MM-dd");
        SimpleDateFormat sdfTime = new SimpleDateFormat("HH:mm:ss");
        return sdfDate.format(cal.getTime()) + "T" + sdfTime.format(cal.getTime());
	}

	public String getFulfilmentSiteID(){
		String fulfilmentSiteID = rootNode[Constants.ordnsRes.FulfilmentSiteID].text();
		return fulfilmentSiteID;
	}

	public String getProcessingStatus(){
		String failScenarioKey = getItemId() + "_" + getShippingMethodCode();
		FailureScenario failureScenario = Constants.unavailableItemsMap.get(failScenarioKey);
		if(failureScenario != null && (failureScenario instanceof CCSDFailureScenario)){
			CCSDFailureScenario ccsdFailureScenario = (CCSDFailureScenario)failureScenario;
			if(("2082".equals(ccsdFailureScenario.getErrorCode()) || "2081".equals(ccsdFailureScenario.getErrorCode()))
				&& (ccsdFailureScenario.getUnavailableStores()).contains(getFulfilmentSiteID())){
					return "Unavailable";
			}
		}
		else if(failureScenario != null && (failureScenario instanceof HDZSDFailureScenario)){
			HDZSDFailureScenario hdZsdFailureScenario = (HDZSDFailureScenario)failureScenario;
			if(("2082".equals(hdZsdFailureScenario.getErrorCode()) || "2081".equals(hdZsdFailureScenario.getErrorCode()))
				&& (hdZsdFailureScenario.getUnavailableStores()).contains(getFulfilmentSiteID())){
					return "Unavailable";
			}
		}
		else if(failureScenario != null && (failureScenario instanceof CCNDFailureScenario)){
			CCNDFailureScenario ccndFailureScenario = (CCNDFailureScenario)failureScenario;
			if(ccndFailureScenario.getErrorCode() != null && ccndFailureScenario.getErrorCode().trim().length() > 0){
				return "Unavailable";
			}
		}
		else if(failureScenario != null && (failureScenario instanceof HDZVHFailureScenario)){
			HDZVHFailureScenario hdZvhFailureScenario = (HDZVHFailureScenario)failureScenario;
			if(hdZvhFailureScenario.getErrorCode() != null && hdZvhFailureScenario.getErrorCode().trim().length() > 0){
				return "Unavailable";
			}
		}

		else if(failureScenario != null && (failureScenario instanceof HDZBKSFailureScenario)){
		    HDZBKSFailureScenario hdZbksFailureScenario = (HDZBKSFailureScenario)failureScenario;
		    if(hdZbksFailureScenario.getErrorCode() != null && hdZbksFailureScenario.getErrorCode().trim().length() > 0){
			return "Unavailable";
		    }
		}

			return "Success";
	}
}

public class FulfilmentSourceHeaderRequestTag extends AbstractGenericRequestTag{

	private String headerStatusCode;
	private String headerStatusDescription;
	private String headerStatusType;

	public FulfilmentSourceHeaderRequestTag(def node,List fulfilmentSourceLineRequestTagList){
		this.rootNode = node;
		int noOfFailures = 0;
		for(def fulfilmentSourceLineRequestTag : fulfilmentSourceLineRequestTagList){
			InvokedVariablesContainer.log.info("###########################  PROCESSING STATUS For Line: " + fulfilmentSourceLineRequestTag.getLineNumber());
			InvokedVariablesContainer.log.info(fulfilmentSourceLineRequestTag.getProcessingStatus());
			if("Unavailable".equals(fulfilmentSourceLineRequestTag.getProcessingStatus())){
				noOfFailures++;
			}
		}
		InvokedVariablesContainer.log.info("################### NO OF FAILURES: " + noOfFailures);
		if(noOfFailures == 0){
			headerStatusCode = "0000";
			headerStatusDescription = "Successful Order";
			headerStatusType = "SUCCESS";
		}
		else if(noOfFailures < fulfilmentSourceLineRequestTagList.size()){
			headerStatusCode = "0001";
			headerStatusDescription = "Partially Processed Order";
			headerStatusType = "PARTIAL";
		}
		else{
			headerStatusCode = "9999";
			headerStatusDescription = "Failure Order";
			headerStatusType = "FAILURE";
		}

		InvokedVariablesContainer.log.info("################### STATUS CODE: " + headerStatusCode);
		InvokedVariablesContainer.log.info("################### STATUS DESCRIPTION: " + headerStatusDescription);
		InvokedVariablesContainer.log.info("################### STATUS TYPE: " + headerStatusType);
	}

	public boolean isNodeDeletable(String hierarchy){
		return false;
	}

	public String getHeaderDocumentId(){
		def headerDocumentId = rootNode[Constants.ordnsRes.SalesOrderReference][Constants.oansRes.DocumentID][Constants.oansRes.ID].text();
		return headerDocumentId;
	}

	public String getHeaderStatusCode(){
		return headerStatusCode;
	}

	public String getHeaderStatusDescription(){
		return headerStatusDescription;
	}

	public String getHeaderStatusType(){
		return headerStatusType;
	}
}
