public interface DynamicGenericTemplateStrategy{
	String getDynamicContent();
}

public abstract class AbstractDynamicGenericTemplateStrategy implements DynamicGenericTemplateStrategy{
	protected GenericRequestTagInterface genericRequestTag;
	protected def rootNodeResponse;
	protected def distinguishingPartOfDynaKey;

	private String getNodeHierarchy(def node){
		def tempNode = node;
		List hierarchyNodesHolder = new ArrayList();

		while(tempNode != rootNodeResponse){
			hierarchyNodesHolder.add(tempNode);
			tempNode = tempNode.parent();
		}
		hierarchyNodesHolder.add(tempNode);

		StringBuilder hierarchyStrBuilder = new StringBuilder("");
		for(int index=hierarchyNodesHolder.size()-1;index >= 0;index-- ){
			Node nodeTemp = (Node)(hierarchyNodesHolder.get(index));
			String nodeName = ((QName)nodeTemp.name()).getLocalPart();
			if(hierarchyStrBuilder.length() == 0){
				hierarchyStrBuilder.append(nodeName);
			}
			else{
				hierarchyStrBuilder.append("_" + nodeName);
			}
		}

		return hierarchyStrBuilder.toString();
	}

	private void createDynamicXmlBlockWithPlaceHolders(rootNode) {
		InvokedVariablesContainer.log.info("################# prasenjit 8888888888888 ");
		if(rootNode instanceof NodeList){
			for(def childNode : rootNode){
				createDynamicXmlBlockWithPlaceHolders(childNode);
			}
		}
		else if(rootNode instanceof Node){
			InvokedVariablesContainer.log.info("################# prasenjit 999999999999999999999999 ");
			if(rootNode.children().size()> 0){
				 InvokedVariablesContainer.log.info("################# prasenjit 10 ");
				for (Iterator iterator = rootNode.children().iterator(); iterator.hasNext();){
					InvokedVariablesContainer.log.info("################# prasenjit 11 ");
					def child = iterator.next();
					if(genericRequestTag.isNodeDeletable(getNodeHierarchy(child))){
						iterator.remove();
						rootNode.remove(child);
					}
					else if(((child.children().size() == 1) && (child.children().get(0) instanceof java.lang.String)) || child.children().size()==0){
						InvokedVariablesContainer.log.info("################# prasenjit 12 ");
						String nodeHierarchy = getNodeHierarchy(child);
						InvokedVariablesContainer.log.info("**************** Node Hierarchy: " + nodeHierarchy);
						InvokedVariablesContainer.log.info("**************** Related Property Name: " + Constants.nodeHierarchyToResponseAttributeMap.get(nodeHierarchy));
						if(Constants.nodeHierarchyToResponseAttributeMap.get(nodeHierarchy) != null){
							String responseElementName = Constants.nodeHierarchyToResponseAttributeMap.get(nodeHierarchy);
							InvokedVariablesContainer.log.info("********************  SETTING ELEMENT: " + responseElementName);
							String methodName = "get" + responseElementName;
							try{
								Method method = genericRequestTag.getClass().getMethod(methodName);
								InvokedVariablesContainer.log.info(method);
								InvokedVariablesContainer.log.info(method.invoke(genericRequestTag));
								child.setValue(method.invoke(genericRequestTag));
							}
							catch(IllegalAccessException illegalAccessException){
								InvokedVariablesContainer.log.info("################# EXCEPTION IN SETTING ELEMENT 1: " + illegalAccessException.getMessage());
							}
							catch(SecurityException securityException){
								InvokedVariablesContainer.log.info("################# EXCEPTION IN SETTING ELEMENT 2: " + securityException.getMessage());
							}
							catch(IllegalArgumentException illegalArgumentException){
								InvokedVariablesContainer.log.info("################# EXCEPTION IN SETTING ELEMENT 3: " + illegalArgumentException.getMessage());
							}
							catch(InvocationTargetException invocationTargetException){
								InvokedVariablesContainer.log.info("################# EXCEPTION IN SETTING ELEMENT 4: " + invocationTargetException.getMessage());
							}
						}
					}
					else{
						createDynamicXmlBlockWithPlaceHolders(child);
					}
				}
			}
		}
	}

	private String getDynamicContentAsString(rootNode){
		InvokedVariablesContainer.log.info("################# prasenjit 7777777777777 ");
		createDynamicXmlBlockWithPlaceHolders(rootNode);
		InvokedVariablesContainer.log.info("################# prasenjit 888888888888888888888 ");
		if(rootNode instanceof groovy.util.Node){
			def xmlOutput = new StringWriter();
			def xmlNodePrinter = new XmlNodePrinter(new PrintWriter(xmlOutput));
			xmlNodePrinter.print(rootNode);
			return xmlOutput.toString();
		}
		return "";
	}

	public String getDynamicContent(){
		return trim(getDynamicContentAsString(rootNodeResponse));
	}

	private String trim(String input) {
		InvokedVariablesContainer.log.info("################# prasenjit 5555555555555555 ");
		BufferedReader reader = new BufferedReader(new StringReader(input));
		StringBuffer result = new StringBuffer();

		try {

			String line;
			while ( (line = reader.readLine() ) != null)
				result.append(line.trim());
				return result.toString();
		} catch (IOException e) {
			throw new RuntimeException(e);
		}
		InvokedVariablesContainer.log.info("################# prasenjit 666666666666666666 ");
	}

}


public class DynamicFulfilmentSourceLineTemplateGenerator extends AbstractDynamicGenericTemplateStrategy {

	public DynamicFulfilmentSourceLineTemplateGenerator(GenericRequestTagInterface genericRequestTag){
		this.genericRequestTag = genericRequestTag;
		InvokedVariablesContainer.log.info("#################  CHOOSING XML FOR LINE ITEM " + genericRequestTag.getLineNumber());
		//String templateNameKey = genericRequestTag.getLocalTagName() + "_" + genericRequestTag.getShippingMethodCode() + "_" + genericRequestTag.getProcessingStatus();
		String templateNameKey = genericRequestTag.getLocalTagName() + "_All";
		InvokedVariablesContainer.log.info("#################  TEMPLATE KEY:" + templateNameKey);
		def xmlFile = Constants.dynamicTemplateLocation + Constants.fulfilmentSourceLineTemplateNameMap.get(templateNameKey);
		//def xmlFile = Constants.dynamicTemplateLocation + 'PFS Response Template_All.xml';
		InvokedVariablesContainer.log.info("######################## XML FILE:" + xmlFile);

		XmlParser xmlParser = new XmlParser();
		xmlParser.setTrimWhitespace(true);
		def soapenvEnvelopeRootResponse = xmlParser.parse(xmlFile);
		this.rootNodeResponse = soapenvEnvelopeRootResponse[Constants.soapenvns.Body][Constants.ordnsRes.AcknowledgeFulfilmentSource][Constants.ordnsRes.DataArea][Constants.ordnsRes.FulfilmentSource][Constants.ordnsRes.FulfilmentSourceLine][0];
		this.distinguishingPartOfDynaKey = genericRequestTag.getLineNumber();
	}
}


public class DynamicFulfilmentSourceGroupTemplateGenerator extends AbstractDynamicGenericTemplateStrategy {

	private static Set generatedFulfilmentSourceGrps = new HashSet();

	public DynamicFulfilmentSourceGroupTemplateGenerator(GenericRequestTagInterface genericRequestTag){
		this.genericRequestTag = genericRequestTag;
		InvokedVariablesContainer.log.info("#################  CHOOSING XML SHIPPING GROUP FOR LINE ITEM " + genericRequestTag.getLineNumber());
		String templateNameKey = genericRequestTag.getLocalTagName() + "_" + genericRequestTag.getShippingMethodCode() + "_Group";
		InvokedVariablesContainer.log.info("#################  TEMPLATE KEY:" + templateNameKey);
		def xmlFile = Constants.dynamicTemplateLocation + Constants.fulfilmentSourceLineTemplateNameMap.get(templateNameKey);
		InvokedVariablesContainer.log.info("######################## XML FILE:" + xmlFile);

		XmlParser xmlParser = new XmlParser();
		xmlParser.setTrimWhitespace(true);
		def soapenvEnvelopeRootResponse = xmlParser.parse(xmlFile);
		this.rootNodeResponse = soapenvEnvelopeRootResponse[Constants.soapenvns.Body][Constants.ordnsRes.AcknowledgeFulfilmentSource][Constants.ordnsRes.DataArea][Constants.ordnsRes.FulfilmentSource][Constants.ordnsRes.FulfilmentSourceGroup][0];
		this.distinguishingPartOfDynaKey = genericRequestTag.getShippingMethodCode();
	}

	public String getDynamicContent(){
		if(generatedFulfilmentSourceGrps.contains(genericRequestTag.getShippingMethodCode())){
			InvokedVariablesContainer.log.info("######################## SHIPPING METHOD CODE ALREADY ADDED: " + genericRequestTag.getShippingMethodCode());
			return "";
		}
		else{
			generatedFulfilmentSourceGrps.add(genericRequestTag.getShippingMethodCode());
			return super.getDynamicContent();
		}
	}

	public static void clearGeneratedFulfilmentSourceGrps(){
		generatedFulfilmentSourceGrps.clear();
	}
}


