public class RequestHandler{

		private static String dynamicFulfilmentSourceLineContents = "";
		private static String dynamicFulfilmentSourceGroupContents = "";
		private static String dynamicFulfilmentSourceHearedContents = "";

	 public static String handleRequest(){
	 	try{
	 		initializeForNewRequest();
	 		List<FulfilmentSourceLinesRequestTag> fulfilmentSourceLineRequestTagList = getShuffledFulfilmentSourceLineRequestTags();
	 		if(shouldBeRedirected(fulfilmentSourceLineRequestTagList)){
	 			InvokedVariablesContainer.log.info("****************** Redirection required *********************");
	 			return redirectToExistingMocks();
	 		}
	 		else {
	 			generateDynamicContentForFulfilmentSourceLines(fulfilmentSourceLineRequestTagList);
	 			setDynamicContentsInContext();
	 			return "MockResponse";
	 		}


	 	}
	 	catch(Exception ex){
	 		InvokedVariablesContainer.log.info(" ################## Exception in generating Mock PFS service : " + ex.getMessage());
	 		return "MockResponse";
	 	}

	 }

	 private static boolean shouldBeRedirected(List<FulfilmentSourceLinesRequestTag> fulfilmentSourceLineRequestTagList){

		for(FulfilmentSourceLinesRequestTag fulfilmentSourceLinesRequestTag : fulfilmentSourceLineRequestTagList){
			String shippingMethodCode = fulfilmentSourceLinesRequestTag.getShippingMethodCode();
			if(!Constants.handledShippingCodes.contains(shippingMethodCode)){
				return true;
			}
		}

		return false;
	 }

	 private static String redirectToExistingMocks(){
		def fulfilmentSourceRootNode = Constants.soapenvEnvelopeRootNodeRequest[Constants.soapenvns.Body][Constants.ordnsReq.ProcessFulfilmentSource][Constants.ordnsReq.DataArea][Constants.ordnsReq.FulfilmentSource][0];
		String channelCode = fulfilmentSourceRootNode[Constants.ordnsReq.FulfilmentSourceHeader][Constants.ordnsReq.BrandChannel][Constants.ordnsReq.Channel][Constants.nsnsReq.Code].text();
		InvokedVariablesContainer.log.info("****************** Redirecting for " + channelCode);

		def soapUrl = null;
		if("WEB".equalsIgnoreCase(channelCode)){
			soapUrl = new URL("http://lnxs0514.uk.b-and-q.com:8989/mockws/fulfilmentF2/mockFulfilmentSourceBindingF2?WSDL");
		}
		else{
			soapUrl = new URL("http://lnxs0514.uk.b-and-q.com:8989/mockws/fulfilmentF3/PFSMockF3?WSDL");
		}

		if(soapUrl != null){
			InvokedVariablesContainer.log.info("*************** Actually redirecting to " + soapUrl);
			def soapRequest = InvokedVariablesContainer.mockRequest.requestContent;
			def connection = soapUrl.openConnection();
			connection.setRequestMethod("POST");
			connection.setRequestProperty("Content-Type" ,"text/html");
			connection.setRequestProperty("SOAPAction", "");
			connection.doOutput = true;
			Writer writer = new OutputStreamWriter(connection.outputStream);
			writer.write(soapRequest);
			writer.flush();
			writer.close();
			connection.connect();

			def soapResponse = connection.content.text;
			InvokedVariablesContainer.context.responseMessage=soapResponse;
			return "DefaultMockResponse";
		}
		return "";
	 }

	private static void initializeForNewRequest(){
		Constants.loadConstantsForNewRequest();
		DynamicFulfilmentSourceGroupTemplateGenerator.clearGeneratedFulfilmentSourceGrps();
		dynamicFulfilmentSourceLineContents = "";
		dynamicFulfilmentSourceGroupContents = "";
		dynamicFulfilmentSourceHearedContents = "";
	}

	private static List<FulfilmentSourceLinesRequestTag> getShuffledFulfilmentSourceLineRequestTags(){
		def fulfilmentSourceLineNodeList = Constants.soapenvEnvelopeRootNodeRequest[Constants.soapenvns.Body][Constants.ordnsReq.ProcessFulfilmentSource][Constants.ordnsReq.DataArea][Constants.ordnsReq.FulfilmentSource][Constants.ordnsReq.FulfilmentSourceLine];
		InvokedVariablesContainer.log.info("***************** NO OF LINE ITEMS:" + fulfilmentSourceLineNodeList.size());
		List<FulfilmentSourceLinesRequestTag> fulfilmentSourceLineRequestTagList = new ArrayList<FulfilmentSourceLinesRequestTag>();
		for(def node : fulfilmentSourceLineNodeList){
			FulfilmentSourceLinesRequestTag fulfilmentSourceLinesRequestTag = new FulfilmentSourceLinesRequestTag(node);
			fulfilmentSourceLineRequestTagList.add(fulfilmentSourceLinesRequestTag);
		}

		Collections.shuffle(fulfilmentSourceLineRequestTagList);
		return fulfilmentSourceLineRequestTagList;
	}

	private static void generateDynamicContentForFulfilmentSourceLines(List<FulfilmentSourceLinesRequestTag> fulfilmentSourceLineRequestTagList){
		InvokedVariablesContainer.log.info("################# prasenjit 11111111111111");
		for(FulfilmentSourceLinesRequestTag fulfilmentSourceLinesRequestTag : fulfilmentSourceLineRequestTagList){
			InvokedVariablesContainer.log.info("################# prasenjit 22222222222222222");
			DynamicFulfilmentSourceLineTemplateGenerator dynamicFulfilmentSourceLineTemplateGenerator = new DynamicFulfilmentSourceLineTemplateGenerator(fulfilmentSourceLinesRequestTag);
			InvokedVariablesContainer.log.info("################# prasenjit 33333333333333333333333333");
			dynamicFulfilmentSourceLineContents = dynamicFulfilmentSourceLineContents + dynamicFulfilmentSourceLineTemplateGenerator.getDynamicContent();
			InvokedVariablesContainer.log.info("################# prasenjit 444444444444444444444444");
			generateDynamicContentForFulfilmentSourceGroup(fulfilmentSourceLinesRequestTag);
		}
		generateDynamicContentForFulfilmentSourceHeader(fulfilmentSourceLineRequestTagList);
	}

	private static void generateDynamicContentForFulfilmentSourceGroup(FulfilmentSourceLinesRequestTag fulfilmentSourceLinesRequestTag){
		if("Success".equals(fulfilmentSourceLinesRequestTag.getProcessingStatus())){
				DynamicFulfilmentSourceGroupTemplateGenerator dynamicFulfilmentSourceGroupTemplateGenerator = new DynamicFulfilmentSourceGroupTemplateGenerator(fulfilmentSourceLinesRequestTag);
				dynamicFulfilmentSourceGroupContents = dynamicFulfilmentSourceGroupContents + dynamicFulfilmentSourceGroupTemplateGenerator.getDynamicContent();
		}
	}

	private static void generateDynamicContentForFulfilmentSourceHeader(List<FulfilmentSourceLinesRequestTag> fulfilmentSourceLineRequestTagList){
		def fulfilmentSourceHeaderNodeList = Constants.soapenvEnvelopeRootNodeRequest[Constants.soapenvns.Body][Constants.ordnsReq.ProcessFulfilmentSource][Constants.ordnsReq.DataArea][Constants.ordnsReq.FulfilmentSource][Constants.ordnsReq.FulfilmentSourceHeader];
		FulfilmentSourceHeaderRequestTag fulfilmentSourceHeaderRequestTag = new FulfilmentSourceHeaderRequestTag(fulfilmentSourceHeaderNodeList[0],fulfilmentSourceLineRequestTagList);
		DynamicFulfilmentSourceHeaderTemplateGenerator dynamicFulfilmentSourceHeaderTemplateGenerator = new DynamicFulfilmentSourceHeaderTemplateGenerator(fulfilmentSourceHeaderRequestTag);
		dynamicFulfilmentSourceHearedContents = dynamicFulfilmentSourceHearedContents + dynamicFulfilmentSourceHeaderTemplateGenerator.getDynamicContent();
	}

	private static void setDynamicContentsInContext(){
		InvokedVariablesContainer.context.dynamicFulfilmentSourceLineContents = dynamicFulfilmentSourceLineContents;
		InvokedVariablesContainer.context.dynamicFulfilmentSourceGroupContents = dynamicFulfilmentSourceGroupContents;
		InvokedVariablesContainer.context.dynamicFulfilmentSourceHearedContents = dynamicFulfilmentSourceHearedContents;
		InvokedVariablesContainer.log.info("***************** CREATION DATE TIME:" + Constants.soapenvEnvelopeRootNodeRequest[Constants.soapenvns.Body][Constants.ordnsReq.ProcessFulfilmentSource][Constants.nsnsReq.ApplicationArea][Constants.nsnsReq.CreationDateTime][0].text());
		//InvokedVariablesContainer.context.creationDateTime = Constants.soapenvEnvelopeRootNodeRequest[Constants.soapenvns.Body][Constants.ordnsReq.ProcessFulfilmentSource][Constants.nsnsReq.ApplicationArea][Constants.nsnsReq.CreationDateTime][0].text();
		InvokedVariablesContainer.context.creationDateTime = getCreationDateTimeForResponse();
		InvokedVariablesContainer.context.bodId = Constants.soapenvEnvelopeRootNodeRequest[Constants.soapenvns.Body][Constants.ordnsReq.ProcessFulfilmentSource][Constants.nsnsReq.ApplicationArea][Constants.nsnsReq.BODID][0].text();

		InvokedVariablesContainer.log.info("***************** DYNAMIC LINE ITEM CONTENTS:" + dynamicFulfilmentSourceLineContents);
	}

	private static String getCreationDateTimeForResponse(){
		Date today = new Date();
		Calendar cal = Calendar.getInstance();
		cal.setTime(today);
		SimpleDateFormat sdfDate = new SimpleDateFormat("yyyy-MM-dd");
		SimpleDateFormat sdfTime = new SimpleDateFormat("HH:mm:ss");
		return sdfDate.format(cal.getTime()) + "T" + sdfTime.format(cal.getTime());
	}
}

RequestHandler.handleRequest();
